void k_rect(uint16_t* display, int x, int y, int dx, int dy, uint16_t color){
	for(int i = 0; i<dx; i++){
				for (int j =0; j <dy; j++){
					display[(y+i)*DISPLAY_WIDTH+x + j] = color;
		}
	}
}

void k_fill(uint16_t* display, uint16_t color){
	for(int i =0; i < DISPLAY_HEIGHT*DISPLAY_WIDTH; i++){
		display[i] = color;
	}
}

void k_printchar(uint16_t* display, uint8_t* sim, int x, int y, uint16_t color){
	volatile int tx = 0;
	volatile int ty = 0;
	volatile int el = 1;
	volatile int ch_w = sim[0];
	volatile int ch_h = 19;
	
	for(volatile int tx = 0; tx < ch_w; tx++){
		ty = 0;
		while(ty < ch_h){
			for(volatile int k =0; k < 8; k++){
				if(sim[el] & (1 << k)){
					display[DISPLAY_WIDTH*(y + ty)+x+tx] = color;
				}
				ty++;
				if(ty == 19) k = 8;
			}
			el++;
		}
	}
}

void k_print_string(uint16_t* display, char* str, int x, int y, int space, uint16_t color){
	int tx = x;
	for(int i =0; (i < 25)&(str[i] != '\n'); i++){
		k_printchar(display, A+ (str[i]-32)*55,  tx, y, color);
		tx = tx + A[(str[i]-32)*55] + space;
	}
}

void k_draw(uint16_t* display, int x, int y,  uint16_t color){
	display[DISPLAY_WIDTH*y+x] = color;
}

void k_draw_line(uint16_t* display, int x1, int y1, int x2, int y2,  uint16_t color){
	
	if(x1 > x2) swipe(&x1, &x2);
	if(y1 > y2) swipe(&y1, &y2);
	
	volatile int dx = x2 - x1;
	volatile int dy = y2 - y1;
	volatile float  step, cur;
	
	if (dx == dy){
		while (x1 <= x2){
			k_draw(display, x1++, y1++, color);
		}
	}else if(dx > dy){
		step = (float) dy / dx;
		cur = y1;
		while (x1 <= x2){
			k_draw(display, x1++, (int)(cur+=step), color);
		}
	}else{
		step = (float)dx / dy;
		cur = x1;
		while (y1 <= y2){
			k_draw(display, (int)(cur+=step), y1++, color);
		}
	}
}
	
